import type { Product } from '@/types/product'

const API_BASE = 'https://dummyjson.com'

// Clothing categories whitelist (DummyJSON slugs)
export const ALLOWED_CATEGORIES = [
  'mens-shirts',
  'mens-shoes',
  'mens-watches',
  'womens-dresses',
  'womens-shoes',
  'womens-watches',
  'tops',
  'sunglasses',
  'womens-jewellery',
] as const

export type AllowedCategory = (typeof ALLOWED_CATEGORIES)[number]

function isAllowedCategory(cat: string): cat is AllowedCategory {
  return (ALLOWED_CATEGORIES as readonly string[]).includes(cat)
}

/** Shape returned by DummyJSON for a product */
type RawProduct = {
  id: number
  title: string
  description: string
  price: number
  discountPercentage?: number
  rating?: number
  stock?: number
  brand?: string
  category: string
  thumbnail?: string
  images?: string[]
}

/** List endpoints return an object with a `products` array */
type ProductsResponse = {
  products: RawProduct[]
}

