/** ---------- Normalizer for remote records ---------- */
function normalizeProduct(product: RawProduct): Product {
  // Gender from category
  let gender: Product['gender'] = 'unisex'
  if (product.category.includes('mens')) gender = 'men'
  else if (product.category.includes('womens')) gender = 'women'

  // Always provide Sâ€“XL
  const sizes: Product['sizes'] = ['S', 'M', 'L', 'XL']

  // Safe image/thumbnail fallbacks
  const images: string[] =
    product.images && product.images.length > 0
      ? product.images
      : product.thumbnail
        ? [product.thumbnail]
        : []

  const thumbnail: string = product.thumbnail ?? images[0] ?? ''

  return {
    id: product.id,
    title: product.title,
    brand: product.brand ?? 'Fashion Brand',
    category: product.category,
    description: product.description,
    price: product.price,
    discountPercentage: product.discountPercentage ?? 0,
    images,
    rating: product.rating ?? 4.0,
    stock: product.stock ?? 50,
    gender,
    sizes,
    onSale: (product.discountPercentage ?? 0) > 0,
    thumbnail,
  }
}

function dedupeById(list: Product[]): Product[] {
  const seen = new Set<number>()
  const out: Product[] = []
  for (const p of list) {
    if (!seen.has(p.id)) {
      seen.add(p.id)
      out.push(p)
    }
  }
  return out
}

/** ---------- API ---------- */
export async function fetchAllProducts(): Promise<Product[]> {
  try {
    const response = await fetch(`${API_BASE}/products?limit=100`)
    const data: ProductsResponse = await response.json()

    const remote = (data?.products ?? [])
      .filter((p) => isAllowedCategory(p.category))
      .map(normalizeProduct)

    // Merge remote with local seed
    return dedupeById([...remote, ...ALL_LOCAL])
  } catch (error) {
    console.error('Error fetching products:', error)
    // At least return local seed so pages are populated
    return [...ALL_LOCAL]
  }
}

export async function fetchProductById(id: number): Promise<Product | null> {
  // Fast path: local
  const local = ALL_LOCAL.find((p) => p.id === id)
  if (local) return local

  try {
    const response = await fetch(`${API_BASE}/products/${id}`)
    if (!response.ok) return null

    const product: RawProduct = await response.json()
    if (!isAllowedCategory(product.category)) return null

    return normalizeProduct(product)
  } catch (error) {
    console.error('Error fetching product:', error)
    return null
  }
}

export async function fetchProductsByCategory(
  category: AllowedCategory
): Promise<Product[]> {
  // local items for this category
  const local = ALL_LOCAL.filter((p) => p.category === category)

  try {
    const response = await fetch(`${API_BASE}/products/category/${category}`)
    const data: ProductsResponse = await response.json()

    const remote = (data?.products ?? [])
      .filter((p) => isAllowedCategory(p.category))
      .map(normalizeProduct)

    return dedupeById([...remote, ...local])
  } catch (error) {
    console.error('Error fetching products by category:', error)
    return local
  }
}

export async function searchProducts(query: string): Promise<Product[]> {
  const q = query.trim().toLowerCase()

  // search in local
  const localMatches = ALL_LOCAL.filter((p) => {
    return (
      p.title.toLowerCase().includes(q) ||
      p.brand.toLowerCase().includes(q) ||
      p.category.toLowerCase().includes(q) ||
      p.description.toLowerCase().includes(q)
    )
  })

  try {
    const response = await fetch(
      `${API_BASE}/products/search?q=${encodeURIComponent(query)}`
    )
    const data: ProductsResponse = await response.json()

    const remote = (data?.products ?? [])
      .filter((p) => isAllowedCategory(p.category))
      .map(normalizeProduct)

    return dedupeById([...remote, ...localMatches])
  } catch (error) {
    console.error('Error searching products:', error)
    return localMatches
  }
}
